import {
  BrowserRouter as Router,
  Routes,
  Route,
} from "react-router-dom";

import Home from './pages/Home.js'
import CustomersList from './pages/Customers/List.js'
import TemplateClean from './templates/Clean'
import TemplatePage from './templates/Page.js'
import CustomersRegister from './pages/Customers/Register.js'
import CustomersEdit from './pages/Customers/Edit.js'
import Login from './pages/Login'

import AuthProvider from './state/auth'

const App = () => {

  const logado = true

  return (

    <AuthProvider>
      <Router>
          <Routes>

            <Route 
              path='/login' 
              element={
                <TemplateClean 
                  title='Acesso Restrito'
                  Component={Login}
                />
              } 
            />  

            <Route 
              path='/' 
              element={
                logado ? <TemplatePage title='Pagina Inicial' Component={Home} />
                : <TemplateClean title='Acesso Restrito' Component={Login} />
              } 
            />

            <Route 
              path='/customers' 
              element={
                <TemplatePage 
                  title='Clientes' 
                  Component={CustomersList} 
                />
              }
            />

            <Route 
              path='/customers/add' 
              element={
                <TemplatePage 
                  title='Cadastro de Clientes' 
                  Component={CustomersRegister} 
                />
              }
            />

            <Route 
              path='/customers/edit/:id' 
              element={
                <TemplatePage 
                  title='Editar Cliente' 
                  Component={CustomersEdit} 
                />
              }
            />

        </Routes>
      </Router>
    </AuthProvider>

  );
}

export default App;
