import {useState} from 'react'
import axios from 'axios'
import {TextField, Button} from '@mui/material';
import './Register.css'
import Toasty from '../../components/Toasty.js'

const Register = () => {

    const [form,setForm] = useState({
        name:{
            value:'',
            error:false,
        },
        job:{
            value:'',
            error:false,
        },
    })

    const [openToasty,setOpenToasty] = useState(false)

    const [isLoading,setIsLoading] = useState(false)

    const handleInputChange = (e) =>{
        const {name,value} = e.target

        setForm({
            ...form,
            [name]:{
                value,
            }
        })
    }

    const handleRegisterButton = () => {
        setIsLoading(true)

        let hasError=false
        let newFormState = {
            ...form,
        }

        if(!form.name.value){
            hasError = true

            newFormState.name = {
                value:form.name.value,
                error:true,
                helperText:"Digite o campo Nome corretamente"
            }
        }

        if(!form.job.value){
            hasError = true

            newFormState.job = {
                value:form.job.value,
                error:true,
                helperText:"Digite o campo Cargo corretamente"
            }
        }

        if(hasError){
            return setForm(newFormState)
        }

        axios.post("https://reqres.in/api/users",{
            name:form.name.value,
            job:form.job.value,
        }).then((response) => {
            setOpenToasty(true)
            setIsLoading(false)
        })

    }

    return(
        <>
            <div>
                <TextField
                    error={form.name.error}
                    helperText={form.name.error ? form.name.helperText : ''}
                    label='Digite o seu Nome' 
                    variant='standard' 
                    name='name' 
                    value={form.name.value} 
                    onChange={handleInputChange}
                />
            </div>

            <div>
                <TextField 
                    error={form.job.error}
                    helperText={form.job.error ? form.job.helperText : ''}
                    label='Digite o seu Cargo' 
                    variant='standard' 
                    name='job' 
                    value={form.job.value} 
                    onChange={handleInputChange}
                />
            </div>

            <div>
                <Button variant='contained' color='primary' onClick={handleRegisterButton} disabled={isLoading}>
                    {isLoading ? "Aguarded..." : "Cadastrar"}
                </Button>
            </div>
            <Toasty open={openToasty} onClose = {!openToasty} severity='success' message='cadastro realizado com sucesso'/>
        </>
    )
}

export default Register
